<?php$Create = false;$Delete = false;if (function_exists('check_authorize')){     if (check_authorize("announcementrelease"))    {        $Create = true;        $Delete = true;    }    else    {		global  $supplierusertype, $supplierid;		if ($supplierusertype != "superadmin" && $supplierusertype != "admin")		{		    if (isset($supplierid) && $supplierid != "" && $supplierid != "0")		    { 			        $Create = true;			        $Delete = true; 		    } 		}      }} $actionmapping = array(    array('actionname' => 'SimulateApply','securitycheck' => '1','type'=>'button','func'=>'check_SimulateApply'),);  if (!function_exists('check_SimulateApply')) {		function check_SimulateApply($module,$focus)    {                     if($focus->column_fields['approvalstatus'] != '2' && $focus->mode == 'edit')            {                return '<a  class="btn btn-default" data-toggle="dialog" data-icon="lock" data-mask="true" data-maxable="false" data-resizable="false" href="index.php?module='.$module.'&amp;action=SimulateApply&amp;record='.$focus->id.'" > 发布</a>';            }            else            {                return '<a disabled class="btn btn-default" data-icon="lock" href="javascript:;" > 发布</a>';            }     }}?>