<?php$Create = false;$Delete = true;$MassEdit = false;$CustomMassDelete = true;$ExportExcel = true;$actionmapping = array (    array('actionname' => 'ChangeCertificate','securitycheck' => '1','type'=>'button','func'=>'check_ChangeCertificate'),    array('actionname' => 'approvalModify','securitycheck' => '1','type'=>'button','func'=>'check_approvalModify'),);if (!function_exists('check_ChangeCertificate')) {    function check_ChangeCertificate($module,$focus)    {        $record=$focus->folumn_fields['record'];        $query=XN_Query::create("Content")            ->tag("ma_staffs")            ->filter("type","eic","ma_staffs")            ->filter("my.record","=",$record)            ->filter("my.profileid","=",XN_Profile::$VIEWER)            ->filter("my.deleted","=","0")            ->end(1)            ->execute();        if (count($query))        {            $modifystatus=$focus->column_fields['modifystatus'];            if( $modifystatus=='1' || $modifystatus==""){                return '<a  class="btn btn-default" data-toggle="navtab" data-icon="lock" href="index.php?module=Ma_CertificateModify&action=EditView&parent_id='.$focus->column_fields['record'].'&from='.$module.'&mode_type=create" >修改资质</a>';            }        }    }}?>