<?php global  $supplierid,$supplierusertype;   if(isset($_SESSION['supplierid']) && $_SESSION['supplierid'] !='' &&  isset($_SESSION['supplierusertype']) && $_SESSION['supplierusertype'] !=''){	if($_SESSION['supplierid'] != "0" )	{		$supplierid = $_SESSION['supplierid'];		$supplierusertype = $_SESSION['supplierusertype'];	}}else{	$supplier_users = XN_Query::create ( 'Content' ) ->tag('supplier_users')	    ->filter ( 'type', 'eic', 'supplier_users')	    ->filter ( 'my.deleted', '=', '0' )	    ->filter ( 'my.profileid', '=' ,XN_Profile::$VIEWER)	    ->execute();	if (count($supplier_users) > 0)	{	  	  	$supplier_user_info = $supplier_users[0];			$supplierid = $supplier_user_info->my->supplierid;			$supplierusertype = $supplier_user_info->my->supplierusertype;			$_SESSION['supplierid'] = $supplierid;			$_SESSION['supplierusertype'] = $supplierusertype;	}	else	{		$supplierid = '0';		$supplierusertype = '';	}}global  $vendorid; if(isset($_SESSION['vendorid']) && $_SESSION['vendorid'] != "" ){	if($_SESSION['vendorid'] != "0" )	{		$vendorid = $_SESSION['vendorid'];	} }else{	$mall_vendors = XN_Query::create ( 'Content' ) ->tag('mall_vendors')		->filter ( 'type', 'eic', 'mall_vendors')		->filter ( 'my.deleted', '=', '0' )		->filter ( 'my.supplierid', '=' ,$supplierid)		->filter ( 'my.profileid', '=' ,XN_Profile::$VIEWER)		->execute();	if (count($mall_vendors) > 0)	{		$mall_vendor_info = $mall_vendors[0];		$vendorid= $mall_vendor_info->id;		$_SESSION['vendorid'] = $vendorid;	} 	else	{		$_SESSION['vendorid'] = "0";	}}?>