<?php$Create = true;$Delete = true;$MassEdit = false;$MassSendApprove=false;$CustomMassDelete = false;$ExportExcel = false;$actionmapping = array (    array('actionname' => 'enable','securitycheck' => '1','func'=>'isCanOn','type'=>'listview'),    array('actionname' => 'disable','securitycheck'=>'1','func'=>'isCanOff','type'=>'listview'),    array('actionname' => 'SimulateApply','securitycheck' => '1','type'=>'button','func'=>'check_SimulateApply'),    array('actionname' => 'bind','securitycheck' => '1','type'=>'button','func'=>'check_bind'),);if (!function_exists('check_SimulateApply')) {    function check_SimulateApply($module,$focus)    {        global $current_user,$supplierid,$supplierusertype;        if (isset($supplierid) && $supplierid != "" && $supplierusertype=='boss' && $focus->column_fields['mall_vendorsaddressstatus'] == "JustCreated")        {            if($focus->mode=='edit'){                if ($focus->column_fields['approvalstatus'] != '2')                {                    return '<a class="btn btn-default" data-toggle="dialog" data-icon="lock" data-mask="true" data-maxable="false" data-resizable="false" href="index.php?module='.$module.'&amp;action=SimulateApply&amp;record='.$focus->id.'" > 提交上线</a>';                }                else                {                    return '<a disabled class="btn btn-default" data-icon="lock" href="javascript:;" > 提交上线</a>';                }            }else{                return '';            }        }    }}if (!function_exists('isCanOn')) {    function isCanOn(){        global $currentModule;        return '<a data-id="edit" class="btn btn-default" data-group="ids" data-icon="edit" data-toggle="doajaxchecked" href="index.php?module='.$currentModule.'&action=enable" data-title="确实要启用吗?"><span>启用</span></a>';    }}if (!function_exists('isCanOff')) {    function isCanOff(){        global $currentModule;        return '<a data-id="edit" class="btn btn-default" data-group="ids" data-icon="edit" data-toggle="doajaxchecked" href="index.php?module='.$currentModule.'&action=disable" data-title="确实要停用吗?"><span>停用</span></a>';    }}if (!function_exists('check_bind')){    function check_bind($module,$focus)    {        //从这里只能创建资质，从厂家信息和经营企业、医疗机构信息里面可以跟注册用户绑定        global  $supplierid,$supplierusertype,$vendorid;        //if($supplierid==$focus->column_fields['supplierid'] && $supplierusertype=="boss"){        if($supplierid==$focus->column_fields['supplierid']){            $remodule="Supplier_Profile";            return '            <script type="text/javascript">                function ma_tomodule_callback(groupid,agrs){                    var url="index.php?module='.$module.'&action=bind&record='.$focus->id.'&relation_id="+agrs.id;                    $.post(url,"",function(data){                        eval("var json="+data);                        if(json.statusCode==200)                        {                            $(this).alertmsg("ok", "绑定成功!");                            $(this).navtab("closeTab","edit") ;                            $(this).navtab("refresh","' . $module . '") ;                        }                        else                        {                            $(this).alertmsg("error", json.message);                        }                    })                }            </script>            <a  class="btn btn-default" data-icon="lock" data-width="800" data-callback="ma_tomodule_callback" data-toggle="lookupbtn" data-url="index.php?module='.$remodule.'&action=Popup&popuptype='.$module.'" >绑定微信</a>';        }    }}?>