<?php/** * securitycheck:1表示不进行权限校验 */	$Create = true;	$Delete = true;		if (!function_exists('check_SimulateApply')) {			function check_SimulateApply($module,$focus)	    {  			if ($focus->column_fields['author'] == XN_Profile::$VIEWER && $focus->column_fields['supplier_newsstatus'] == "JustCreated")			{				if ($focus->column_fields['approvalstatus'] != '2')				{					return '<a class="btn btn-default" data-toggle="dialog" data-icon="lock" data-mask="true" data-maxable="false" data-resizable="false" href="index.php?module='.$module.'&amp;action=SimulateApply&amp;record='.$focus->id.'" > 提交上线</a>';				}				else				{					return '<a disabled class="btn btn-default" data-icon="lock" href="javascript:;" > 提交上线</a>';				}			} 		}	}			if (!function_exists('isCanOn')) {	    function isCanOn(){	        return '<a class="btn btn-default" data-group="ids" data-toggle="doajaxchecked" href="index.php?module=Supplier_News&action=enable" data-title="确实要启用吗?"><i class="fa fa-edit"></i>启用</a>';	    }	}	if (!function_exists('isCanOff')) {	    function isCanOff(){	        return '<a class="btn btn-default" data-group="ids" data-toggle="doajaxchecked" href="index.php?module=Supplier_News&action=disable" data-title="确实要停用吗?"><i class="fa fa-edit"></i>停用</a>';	    }	}		$actionmapping = array (	    array('actionname' => 'enable','securitycheck' => '1','func'=>'isCanOn','type'=>'listview'),	    array('actionname' => 'disable','securitycheck'=>'1','func'=>'isCanOff','type'=>'listview'),		array('actionname' => 'SimulateApply','securitycheck' => '1','type'=>'button','func'=>'check_SimulateApply'),	);?>