<?php/** * securitycheck:1表示不进行权限校验 */$Create = false;$Delete = false;  if (!function_exists('check_SimulateApply')) {	function check_SimulateApply($module,$focus)    { 		global  $supplierid,$supplierusertype;		if (isset($supplierid) && $supplierid != "" && $focus->column_fields['supplier_physicalstoresstatus'] == "JustCreated")		{			if ($focus->column_fields['approvalstatus'] != '2')			{				return '<a class="btn btn-default" data-toggle="dialog" data-icon="lock" data-mask="true" data-maxable="false" data-resizable="false" href="index.php?module='.$module.'&amp;action=SimulateApply&amp;record='.$focus->id.'" > 提交上线</a>';			}			else			{				return '<a disabled class="btn btn-default" data-icon="lock" href="javascript:;" > 提交上线</a>';			}		} 	}}  if (!function_exists('ModifyPhysicalStore')) {    function ModifyPhysicalStore(){        global  $supplierid,$supplierusertype;				$mall_settings = XN_Query::create ( 'Content' ) ->tag('mall_settings')		    ->filter ( 'type', 'eic', 'mall_settings') 			->filter ( 'my.supplierid', '=',$supplierid)			->filter ( 'my.allowphysicalstore', '=','0')		    ->filter ( 'my.deleted', '=', '0' )			->end(1)		    ->execute ();		if ( count($mall_settings) > 0)		{	        if (isset($supplierid) && $supplierid != "" )	            return '<a href="index.php?module=Supplier_PhysicalStores&action=ModifyPhysicalStore" data-title="修改实体店配置" data-height="600" data-width="950" class="btn btn-default" data-group="ids" data-id="ModifyPhysicalStore" data-icon="edit" data-toggle="doajaxchecked" data-dialog=true>修改实体店配置</a>';	        else	            return "";		}		else		{			 return "";		}    }}$actionmapping = array (   	array('actionname' => 'BaiduMap','securitycheck' => '1','type'=>'ajax','location'=>'bottom'),   	array('actionname' => 'ModifyPhysicalStore','securitycheck' => '1','func'=>'ModifyPhysicalStore','type'=>'listview'),	array('actionname' => 'SimulateApply','securitycheck' => '1','type'=>'button','func'=>'check_SimulateApply'),);?>