<?php	/**	 * securitycheck:1表示不进行权限校验	 */	$Create          = false;	$Delete          = false;	$ExportExcel     = false;	$MassSendApprove = true;	$actionmapping = array (		array ('actionname' => 'EditView', 'securitycheck' => '1', 'func' => 'check_CreateReport', 'type' => 'listview'),		array ('actionname' => 'ReportUser', 'securitycheck' => '1', 'func' => 'check_ReportUser', 'type' => 'listview'),		array ('actionname' => 'ManagerCategorys', 'securitycheck' => '1', 'func' => 'check_ManagerCategorys', 'type' => 'listview'),		array ('actionname' => 'ExportTemplate', 'securitycheck' => '1', 'func' => 'check_ExportTemplate', 'type' => 'listview'),	);	if (!function_exists('check_CreateReport'))	{		function check_CreateReport()		{			global $supplierid;			if (isset($supplierid) && $supplierid != "0" && $supplierid != "" && $supplierid == '71352')			{				return '<a class="btn btn-default" data-icon="edit" data-toggle="dialog" data-id="CreateReport" data-mask="true" data-maxable="false" data-resizable="false" data-width="450" data-height="210" href="index.php?module=Supplier_ReportSettings&action=CreateReport" > 新建报表</a>';			}			else			{				return false;			}		}	}	if(!function_exists('check_ManagerCategorys')) {		function check_ManagerCategorys() {			global $supplierid;			if (isset($supplierid) && $supplierid != "0" && $supplierid != "" && $supplierid == '71352')			{				return '<a class="btn btn-default" data-id="edit" data-icon="edit" data-toggle="navtab" href="index.php?module=Supplier_ReportSettings&action=ManagerCategorys" > 报表分类管理</a>';			}			else			{				return false;			}		}	}	if(!function_exists('check_ExportTemplate')) {		function check_ExportTemplate() {			global $supplierid;			if (isset($supplierid) && $supplierid != "0" && $supplierid != "" && $supplierid == '71352')			{				return '<a type="button" class="btn btn-default" href="index.php?module=Supplier_ReportSettings&action=ExportTemplate" data-toggle="doajax" data-confirm-msg="确定要导出所有报表配置吗？">导出报表配置</a>';			}else{				return false;			}		}	}	if(!function_exists('check_ReportUser')) {		function check_ReportUser(){			global $supplierid;			if (isset($supplierid) && $supplierid != "0" && $supplierid != "")			{				return '<a data-callback="supplier_reportsettings_doauthorize_callback" data-title="请选择用户" class="btn btn-default lookupbtn" data-icon="user-secret"  data-checkgroup="ids"  data-toggle="lookupbtn"  data-id="Profiles" data-mask="true" data-maxable="false" data-resizable="false" href="index.php?module=Public&action=SelectRoleUser&mode=checkbox&selectids=" > 指定用户</a>';			}			else			{				return false;			}		}	}