<?php/** * securitycheck:1表示不进行权限校验 */$Create = false;$Delete = false;$actionmapping = array (    array('actionname'=>'EditView','securitycheck' => '1','func'=>'check_add','type'=>'listview'),    array('actionname' => 'enable','securitycheck' => '1','func'=>'isenable','type'=>'listview'),    array('actionname' => 'disable','securitycheck'=>'1','func'=>'isdisable','type'=>'listview'),	 array('actionname'=>'ModifyUserInfo','securitycheck' => '1','func'=>'ModifyUserInfo','type'=>'listview'),    array('actionname' => 'changepassword','securitycheck' => '1','type'=>'button','func'=>'ischangepassword'),     array('actionname' => 'SimulateApply','securitycheck' => '1','type'=>'button','func'=>'check_SimulateApply'));if (!function_exists('ModifyUserInfo')) {    function ModifyUserInfo(){         global $current_user,$supplierid,$supplierusertype;        if (isset($supplierid) && $supplierid != '' && $supplierusertype=='boss')	        return '<a href="index.php?module=Supplier_Users&action=ModifyUserInfo" data-title="修改员工基本信息" data-height="400" data-width="660" data-id="edit" class="btn btn-default" data-group="ids" data-icon="edit" data-toggle="doajaxchecked" data-dialog=true><span>修改员工基本信息</span></a>';         else            return "";     }}if (!function_exists('check_SimulateApply')) {    function check_SimulateApply($module,$focus)    {        global $current_user,$supplierid,$supplierusertype;        if (isset($supplierid) && $supplierid != "" && $supplierusertype=='boss' && $focus->column_fields['supplier_usersstatus'] == "JustCreated")        {            if($focus->mode=='edit'){                if ($focus->column_fields['approvalstatus'] != '2')                {                    return '<a class="btn btn-default" data-toggle="dialog" data-icon="lock" data-mask="true" data-maxable="false" data-resizable="false" href="index.php?module='.$module.'&amp;action=SimulateApply&amp;record='.$focus->id.'" > 提交上线</a>';                }                else                {                    return '<a disabled class="btn btn-default" data-icon="lock" href="javascript:;" > 提交上线</a>';                }            }else{                return '';            }        }    }}if (!function_exists('check_add')) {    function check_add() {        global $currentModule,$supplierid,$supplierusertype;        if(isset($supplierid) && $supplierid !="" && $supplierusertype=='boss'){            return '<a data-id="edit" class="btn btn-default" data-icon="edit" data-toggle="navtab" data-title="新建" href="index.php?module='.$currentModule.'&action=EditView"><i class="fa fa-edit"></i> 新建</a>';        }else{            return false;        }    }}if (!function_exists('isenable')) {    function isenable(){        global $currentModule,$supplierid,$supplierusertype;        if(isset($supplierid) && $supplierid !="" && $supplierusertype=='boss'){            return '<a data-id="edit" class="btn btn-default" data-group="ids" data-icon="edit" data-toggle="doajaxchecked" href="index.php?module='.$currentModule.'&action=enable" data-title="确实要启用此员工?"><span>启用</span></a>';        }else{            return false;        }    }}if (!function_exists('isdisable')) {    function isdisable(){        global $currentModule,$supplierid,$supplierusertype;        if(isset($supplierid) && $supplierid !="" && $supplierusertype=='boss'){            return '<a data-id="edit" class="btn btn-default" data-group="ids" data-icon="edit" data-toggle="doajaxchecked" href="index.php?module='.$currentModule.'&action=disable" data-title="确实要停用此员工?"><span>停用</span></a>';        }else{            return false;        }    }}if (!function_exists('ischangepassword')) {    function ischangepassword($module,$focus)    {        global $supplierid,$supplierusertype;        if(isset($supplierid) && $supplierid != "" && $supplierusertype=='boss' && $focus->column_fields['approvalstatus'] == 'Agree')        {            return '<a data-fresh="false" data-height="300" data-width="400"  data-mask="true" data-toggle="dialog" href="index.php?module='.$module.'&action=ChangePassword&record='.$focus->id.'&type=dialog" class="btn btn-default" ><span>修改密码</span></a>';        }    }}?>