<?php$Create = false;$Delete = false;$MassEdit = false;$CustomMassDelete = true;if (!function_exists('check_authorize')) {	$wxchannels = XN_Query::create('Content_Count')			->tag('wxchannels')			->filter('type','eic','wxchannels')			->filter('my.deleted','=','0')			->filter('author','=',XN_Profile::$VIEWER) 									->rollup() 			->begin(0)			->end(-1);	$wxchannels->execute();			$count = $wxchannels->getTotalCount();	if ($count > 100)	{		$Create = false;	}} if (!function_exists('check_ShowHelp')) {	function check_ShowHelp($module,$focus) {		if ($focus->mode == 'edit') 		{			//return array("visible"=>false);			return array("visible"=>true);		}		else		{			return array("visible"=>true);		}	}}$actionmapping = array (	array('actionname' => 'ShowHelp','securitycheck' => '1','type'=>'ajax','location'=>'bottom','func'=>'check_ShowHelp'),    array ('actionname' => 'ToTopvips','securitycheck' => '1','func'=>'ToTopvips','type'=>'listview'),);if (!function_exists('ToTopvips')) {    function ToTopvips(){		global $currentModule;        if (check_authorize('manageproflie')){            return '<a class="btn btn-default" data-group="ids" data-icon="edit" data-toggle="doajaxchecked" href="index.php?module='.$currentModule.'&action=ToTopvips" data-msg="确实要调整为购物团长吗?"><span>购物团长</span></a>';        }        else            return "";    }}?>